var getAllItems = function() {

	$(""#table_items"").show();
    $(""#loading"").show();

	$.get(baseUrl + ""Authors"", function(data, status)
	{
        if (status == ""success"") {
			$(""#loading"").hide(0.5);
            itemList = data;
            console.log(itemList);
            renderItems(itemList);
        }
        else {
            alert(data);
		}
	});

}

